import "./PersonalInfo.css";
import React from "react";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";
import NavButtons from "../NavButtons/NavButtons.jsx";

const useStyles = makeStyles((theme) => ({
  root: {
    "& > *": {
      marginTop: theme.spacing(2),
      width: "50ch",
    },
  },
}));

const PersonalInfo = (props) => {
  const classes = useStyles();
  const { step, setStep, maxSteps } = props;
  const [values, setValues] = React.useState({
    name: "",
    email: "",
    pno: "",
    github: "",
    linkedin: "",
    website: "",
  });
  const [errors, setErrors] = React.useState({
    email: false,
    empty: false,
    url: false,
  });
  const [errorMessages, setErrorMessages] = React.useState({
    email: "",
    empty: "",
    url: "",
  });

  const handleNext = () => {
    const emailRegex =
      "/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:.[a-zA-Z0-9-]+)*$/";
    const urlRegex =
      "/^((http(s?)?)://)?([wW]{3}.)?[a-zA-Z0-9-.]+.[a-zA-Z]{2,}(.[a-zA-Z]{2,})?$/g";

    if (!values.name.length || !values.pno.length) {
      setErrors((prev) => ({
        ...prev,
        empty: true,
      }));
      setErrorMessages((prev) => ({
        ...prev,
        empty: "This is a required field!",
      }));
    } else if (!values.email.length) {
      setErrors((prev) => ({
        ...prev,
        email: true,
      }));
      setErrorMessages((prev) => ({
        ...prev,
        email: "This is a required field!",
      }));
    } else if (!values.email.match(emailRegex)) {
      setErrors((prev) => ({
        ...prev,
        email: true,
      }));
      setErrorMessages((prev) => ({
        ...prev,
        email: "Please enter a valid email address!",
      }));
    } else if (!values.url.match(urlRegex)) {
      setErrors((prev) => ({
        ...prev,
        url: true,
      }));
      setErrorMessages((prev) => ({
        ...prev,
        url: "Please enter a valid URL!",
      }));
    } else {
      setStep(step + 1);
    }
  };

  return (
    <div>
      <Card className="card">
        <CardContent>
          <form className={classes.root} autoComplete="on">
            <TextField
              label="Name"
              variant="outlined"
              error={errors.empty}
              helperText={errors.empty && errorMessages.empty}
              required
            />
            <TextField
              label="E-mail"
              variant="outlined"
              error={errors.email}
              helperText={errors.email && errorMessages.email}
              required
            />
            <TextField
              label="Phone Number"
              variant="outlined"
              error={errors.empty}
              helperText={errors.empty && errorMessages.empty}
              required
            />
          </form>
        </CardContent>
      </Card>

      <Card className="card">
        <CardContent>
          <form className={classes.root} autoComplete="on">
            <TextField
              label="GitHub URL"
              error={errors.url}
              helperText={errors.url && errorMessages.url}
              variant="outlined"
            />
            <TextField
              label="LinkedIn URL"
              error={errors.url}
              helperText={errors.url && errorMessages.url}
              variant="outlined"
            />
            <TextField
              label="Website URL"
              error={errors.url}
              helperText={errors.url && errorMessages.url}
              variant="outlined"
            />
          </form>
        </CardContent>
      </Card>

      <NavButtons {...props} />
    </div>
  );
};

export default PersonalInfo;
