import React from "react";
import NavButtons from "../NavButtons/NavButtons.jsx";
import Experience from "./Experience.jsx";

const Experiences = (props) => {
  const validateInfo = () => {};

  const value = {
    job: "",
    role: "",
    from: "",
    to: "",
    desc: "",
  };
  const error = {
    job: false,
    role: false,
    from: false,
    to: false,
    desc: false,
  };
  const errorMessage = {
    job: "",
    role: "",
    from: "",
    to: "",
    desc: "",
  };
  const [values, setValues] = React.useState({
    e1: value,
    e2: value,
    e3: value,
    e4: value,
  });
  const [errors, setErrors] = React.useState({
    e1: error,
    e2: error,
    e3: error,
    e4: error,
  });
  const [errorMessages, setErrorMessages] = React.useState({
    e1: errorMessage,
    e2: errorMessage,
    e3: errorMessage,
    e4: errorMessage,
  });

  const onChange = (id, value) => {
    values[id] = value;
    setValues({ ...values });
  };

  return (
    <div>
      <Experience
        id="e1"
        onChange={onChange}
        values={values["e1"]}
        errors={errors["e1"]}
        errorMessages={errorMessages["e1"]}
      />
      <Experience
        id="e2"
        onChange={onChange}
        values={values["e2"]}
        errors={errors["e2"]}
        errorMessages={errorMessages["e2"]}
      />
      <Experience
        id="e3"
        onChange={onChange}
        values={values["e3"]}
        errors={errors["e3"]}
        errorMessages={errorMessages["e3"]}
      />
      <Experience
        id="e4"
        onChange={onChange}
        values={values["e4"]}
        errors={errors["e4"]}
        errorMessages={errorMessages["e4"]}
      />
      <NavButtons {...props} validateInfo={validateInfo} />
    </div>
  );
};

export default Experiences;
