import React, { useState } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./App.css";
// import TitleBar from "./components/TitleBar/TitleBar.jsx";
import PersonalInfo from "./components/PersonalInfo/PersonalInfo.jsx";
import Skills from "./components/Skills/Skills.jsx";
import Education from "./components/Education/Education.jsx";
import Experiences from "./components/Experiences/Experiences.jsx";
import Projects from "./components/Projects/Projects.jsx";
import Preview from "./components/Preview/Preview.jsx";
import Home from "./pages/homePage/Home";
import Login from "./pages/login/Login";
import SignUp from "./pages/signup/SignUp";
import resumeTemplate from "./sample-resumes/1.js";
import axios from "axios";
import { saveAs } from "file-saver";
import Information from "./pages/information/Information";

const createAndDownloadPDF = (resume) => {
  axios
    .post("http://localhost:5000/information/create-pdf", resume)
    .then(() => {
      axios
        .get("http://localhost:5000/information/fetch-pdf", {
          responseType: "arraybuffer",
        })
        .then((res) => {
          const pdfBlob = new Blob([res.data], { type: "application/pdf" });
          saveAs(pdfBlob, `${resume["name"]} - Resume.pdf`);
        })
        .catch((err) => {
          console.log(err);
        });
    })
    .catch((err) => {
      console.log(err);
    });
};

const getForm = (
  resume,
  setResume,
  step,
  setStep,
  numberOfExps,
  setNumberOfExps,
  maxSteps
) => {
  switch (step) {
    case 0:
      return (
        <PersonalInfo
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          maxSteps={maxSteps}
        />
      );
    case 1:
      return (
        <Education
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          maxSteps={maxSteps}
        />
      );
    case 2:
      return (
        <Skills
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          maxSteps={maxSteps}
        />
      );
    case 3:
      return (
        <Experiences
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          setNumberOfExps={setNumberOfExps}
          maxSteps={maxSteps}
        />
      );
    case 4:
      return (
        <Projects
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          numberOfExps={numberOfExps}
          maxSteps={maxSteps}
        />
      );
    // case 5:
    //   return <Languages step={step} setStep={setStep} maxSteps={maxSteps} />;
    default: {
      const html = resumeTemplate(resume);

      return (
        <Preview
          step={step}
          setStep={setStep}
          maxSteps={maxSteps}
          html={html}
          resume={resume}
          handleDownload={createAndDownloadPDF}
        />
      );
    }
  }
};

const App = () => {
  const [resume, setResume] = React.useState({});
  const [step, setStep] = React.useState(0);
  const [numberOfExps, setNumberOfExps] = React.useState(0);
  const stepNames = [
    "Personal Info",
    "Education",
    "Skills",
    "Experiences",
    "Projects",
    // "Languages",
    "Preview",
  ];
  const optionalSteps = [3, 4];

  const [userId, setUserId] = useState("");

  function addUser(id) {
    setUserId(id);
  }

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <Home />
        </Route>
        <Route path="/login">
          <Login setUser={addUser} />
        </Route>
        <Route path="/signup">
          <SignUp />
        </Route>
        <Route path="/information/:userId">
          <Information
            step={step}
            stepNames={stepNames}
            optionalSteps={optionalSteps}
            resume={resume}
            setResume={setResume}
            step={step}
            setStep={setStep}
            numberOfExps={numberOfExps}
            setNumberOfExps={setNumberOfExps}
            maxSteps={stepNames.length}
            getForm={getForm}
          />
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
