import React from "react";
import NavButtons from "../NavButtons/NavButtons.jsx";
import Experience from "./Experience.jsx";

const Experiences = (props) => {
  const { step, setStep, setNumberOfExps } = props;
  const value = {
    job: "",
    role: "",
    from: "",
    to: "",
    desc: "",
  };
  const error = {
    job: false,
    role: false,
    from: false,
    to: false,
    desc: false,
  };
  const errorMessage = {
    job: "",
    role: "",
    from: "",
    to: "",
    desc: "",
  };
  const [values, setValues] = React.useState({
    e1: { ...value },
    e2: { ...value },
    e3: { ...value },
    e4: { ...value },
  });
  const [errors, setErrors] = React.useState({
    e1: { ...error },
    e2: { ...error },
    e3: { ...error },
    e4: { ...error },
  });
  const [errorMessages, setErrorMessages] = React.useState({
    e1: { ...errorMessage },
    e2: { ...errorMessage },
    e3: { ...errorMessage },
    e4: { ...errorMessage },
  });
  const [exps, setExps] = React.useState(0);
  const [isValidated, setIsValidated] = React.useState(false);

  const onChange = (id, value) => {
    values[id] = value;
    setValues({ ...values });
  };
  const validateInfo = () => {
    setIsValidated(false);

    for (let exp in values) {
      let newError = {
        job: true,
        role: true,
        from: true,
        to: true,
        desc: true,
      };
      let newErrorMessage = {
        job: "Please provide complete info for this experience!",
        role: "Please provide complete info for this experience!",
        from: "Please provide complete info for this experience!",
        to: "Please provide complete info for this experience!",
        desc: "Please provide complete info for this experience!",
      };

      let flag = false,
        count = 0,
        currExps = 0;
      for (let key in values[exp]) {
        if (values[exp][key].length) {
          flag = true;
          ++count;
          newError[key] = false;
          newErrorMessage[key] = "";
        }
      }

      if (count === 5) ++currExps;
      if (!flag) {
        newError = {
          job: false,
          role: false,
          from: false,
          to: false,
          desc: false,
        };
        newErrorMessage = {
          job: "",
          role: "",
          from: "",
          to: "",
          desc: "",
        };
      }
      errors[exp] = newError;
      errorMessages[exp] = newErrorMessage;
    }

    setExps(currExps);
    setErrors({ ...errors });
    setErrorMessages({ ...errorMessages });
    setIsValidated(true);
  };
  const handleNext = () => {
    let flag = false;
    for (let error of Object.values(errors)) {
      if (Object.values(error).includes(true)) {
        flag = true;
        break;
      }
    }
    if (!flag) {
      console.log("Exps: " + exps);
      setNumberOfExps(exps);
      setStep(step + 1);
    }
  };
  React.useEffect(() => {
    if (isValidated) handleNext();
  }, [isValidated, handleNext]);

  return (
    <div>
      <Experience
        id="e1"
        onChange={onChange}
        values={values["e1"]}
        errors={errors["e1"]}
        errorMessages={errorMessages["e1"]}
      />
      <Experience
        id="e2"
        onChange={onChange}
        values={values["e2"]}
        errors={errors["e2"]}
        errorMessages={errorMessages["e2"]}
      />
      <Experience
        id="e3"
        onChange={onChange}
        values={values["e3"]}
        errors={errors["e3"]}
        errorMessages={errorMessages["e3"]}
      />
      <Experience
        id="e4"
        onChange={onChange}
        values={values["e4"]}
        errors={errors["e4"]}
        errorMessages={errorMessages["e4"]}
      />
      <NavButtons {...props} validateInfo={validateInfo} />
    </div>
  );
};

export default Experiences;
