//           saveAs(pdfBlob, `${resume["name"]} - Resume.pdf`);
//         })
//         .catch((err) => {
//           console.log(err);
//         });
//     })
//     .catch((err) => {
//       console.log(err);
//     });
// };

const getPdfViewer = async (resume) => {
  await axios.post("http://localhost:5000/information/create-pdf", resume);
  const res = await axios.get("http://localhost:5000/information/fetch-pdf", {
    responseType: "arraybuffer",
  });
  const pdfBlob = new Blob([res.data], { type: "application/pdf" });
  const url = URL.createObjectURL(pdfBlob);

  return url;
};

const getPdfViewerHelper = async (resume) => {
  const url = await getPdfViewer(resume);

  return url;
};

const getForm = (
  resume,
  setResume,
  step,
  setStep,
  oldNumberOfExps,
  setOldNumberOfExps,
  numberOfExps,
  setNumberOfExps,
  maxSteps,
  pdfUrl,
  setPdfUrl,
  getUrl,
  setGetUrl
) => {
  switch (step) {
    case 0:
      return (
        <PersonalInfo
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          maxSteps={maxSteps}
        />
      );
    case 1:
      return (
        <Education
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          maxSteps={maxSteps}
        />
      );
    case 2:
      return (
        <Skills
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          maxSteps={maxSteps}
        />
      );
    case 3:
      return (
        <Experiences
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          setOldNumberOfExps={setOldNumberOfExps}
          numberOfExps={numberOfExps}
          setNumberOfExps={setNumberOfExps}
          maxSteps={maxSteps}
        />
      );
    case 4:
      return (
        <Projects
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          oldNumberOfExps={oldNumberOfExps}
          setOldNumberOfExps={setOldNumberOfExps}
          numberOfExps={numberOfExps}
          maxSteps={maxSteps}
          setPdfUrl={setPdfUrl}
          setGetUrl={setGetUrl}
        />
      );
    // case 5:
    //   return <Languages step={step} setStep={setStep} maxSteps={maxSteps} />;
    default: {
      if (getUrl) {
        let url = getPdfViewerHelper(resume);
        url.then((res) => {
          setPdfUrl(res);
          setGetUrl(false);
        });
      }
      const pdfViewer = `<iframe src="${pdfUrl}" type="application/pdf" width=1200px height=1200px></iframe>`;
      console.log(pdfViewer);
      return (
        <Preview
          resume={resume}
          setResume={setResume}
          step={step}
          setStep={setStep}
          maxSteps={maxSteps}
          pdf={pdfViewer}
        />
      );
    }
  }
};

const App = () => {
  const [resume, setResume] = React.useState({});
  const [step, setStep] = React.useState(0);
  const [oldNumberOfExps, setOldNumberOfExps] = React.useState(0);
  const [numberOfExps, setNumberOfExps] = React.useState(0);
  const [pdfUrl, setPdfUrl] = React.useState("");
  const [getUrl, setGetUrl] = React.useState("");
  const stepNames = [
    "Personal Info",
    "Education",
    "Skills",
    "Experiences",
    "Projects",
    // "Languages",
    "Preview",
  ];
  const optionalSteps = [3];

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <Home />
        </Route>
        <Route path="/login">
          <NavBar />
          <Login />
        </Route>
        <Route path="/signup">
          <NavBar />
          <SignUp />
        </Route>
        <Route path="/information/:userId">
          <Information
            resume={resume}
            setResume={setResume}
            step={step}
            stepNames={stepNames}
            optionalSteps={optionalSteps}
            step={step}
            setStep={setStep}
            oldNumberOfExps={oldNumberOfExps}
            setOldNumberOfExps={setOldNumberOfExps}
            numberOfExps={numberOfExps}
            setNumberOfExps={setNumberOfExps}
            maxSteps={stepNames.length}
            pdfUrl={pdfUrl}
            setPdfUrl={setPdfUrl}
            getUrl={getUrl}
            setGetUrl={setGetUrl}
            getForm={getForm}
          />
        </Route>
      </Switch>
    </Router>
  );
};

export default App;
