import React from "react";
import Project from "./Project.jsx";
import NavButtons from "../NavButtons/NavButtons.jsx";

const Projects = (props) => {
  const { step, setStep, numberOfExps } = props;
  const value = {
    title: "",
    link: "",
    desc: "",
  };
  const error = {
    title: false,
    link: false,
    desc: false,
  };
  const errorMessage = {
    title: "",
    link: "",
    desc: "",
  };

  if (numberOfExps === 0) {
    alert(
      "As you haven't entered any experiences, please provoide at least 2 projects below"
    );

    const [values, setValues] = React.useState({
      p1: { ...value },
      p2: { ...value },
      p3: { ...value },
      p4: { ...value },
    });
    const [errors, setErrors] = React.useState({
      p1: { ...error },
      p2: { ...error },
      p3: { ...error },
      p4: { ...error },
    });
    const [errorMessages, setErrorMessages] = React.useState({
      p1: { ...errorMessage },
      p2: { ...errorMessage },
      p3: { ...errorMessage },
      p4: { ...errorMessage },
    });
    const [projects, setProjects] = React.useState(0);
    const [isValidated, setIsValidated] = React.useState(false);

    const onChange = (id, value) => {
      values[id] = value;
      setValues({ ...values });
    };
    const validateInfo = () => {
      setIsValidated(false);

      let currProjects = 0;
      for (let pro in values) {
        let newError = {
          title: true,
          link: true,
          desc: true,
        };
        let newErrorMessage = {
          title: "Please provide complete info for this project!",
          link: "Please provide complete info for this project!",
          desc: "Please provide complete info for this project!",
        };

        let flag = false,
          count = 0;
        for (let key in values[pro]) {
          if (values[pro][key].length) {
            flag = true;
            ++count;
            newError[key] = false;
            newErrorMessage[key] = "";
          }
        }

        if (count === 3) ++currProjects;
        if (!flag) {
          newError = {
            title: false,
            link: false,
            desc: false,
          };
          newErrorMessage = {
            title: "",
            link: "",
            desc: "",
          };
        }
        errors[pro] = newError;
        errorMessages[pro] = newErrorMessage;
      }

      setProjects(currProjects);
      setErrors({ ...errors });
      setErrorMessages({ ...errorMessages });
      setIsValidated(true);
    };
    const handleNext = () => {
      let flag = false;
      for (let error of Object.values(errors)) {
        if (Object.values(error).includes(true)) {
          flag = true;
          break;
        }
      }

      if (projects < 2)
        alert(
          "As you haven't entered any experiences, please provoide at least 2 projects below"
        );
      else if (!flag) setStep(step + 1);
    };
    React.useEffect(() => {
      if (isValidated) handleNext();
    }, [isValidated, handleNext]);

    return (
      <div>
        <Project
          id="p1"
          onChange={onChange}
          values={values["p1"]}
          errors={errors["p1"]}
          errorMessages={errorMessages["p1"]}
        />
        <Project
          id="p2"
          onChange={onChange}
          values={values["p2"]}
          errors={errors["p2"]}
          errorMessages={errorMessages["p2"]}
        />
        <Project
          id="p3"
          onChange={onChange}
          values={values["p3"]}
          errors={errors["p3"]}
          errorMessages={errorMessages["p3"]}
        />
        <Project
          id="p4"
          onChange={onChange}
          values={values["p4"]}
          errors={errors["p4"]}
          errorMessages={errorMessages["p4"]}
        />
        <NavButtons {...props} validateInfo={validateInfo} />
      </div>
    );
  }
};

export default Projects;
